mapper.update:
UPDATE tb_product
product_name=#{productName}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, point=#{point}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus},
product_category_id=#{productCategory.productCategoryId}
WHERE product_id = #{productId} AND shop_id = #{shop.shopId}

mapper.select:
SELECT product_id, product_name, product_desc, img_addr, normal_price, promotion_price, priority, point, create_time, last_edit_time, enable_status, product_category_id, shop_id FROM tb_product
AND shop_id = #{productCondition.shop.shopId}
AND product_category_id = #{productCondition.productCategory.productCategoryId}
AND product_name LIKE CONCAT('%',#{productCondition.productName},'%')
AND enable_status = #{productCondition.enableStatus}
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize}

SELECT
count(1)
FROM tb_product


        AND shop_id = #{productCondition.shop.shopId}


        AND product_category_id = #{productCondition.productCategory.productCategoryId}


        AND product_name LIKE CONCAT('%',#{productCondition.productName},'%')


        AND enable_status = #{productCondition.enableStatus}
